import React from "react";
import { Box, Typography } from "@mui/material";
import Runes from "./Runes";
import { Rune, RuneFamily } from "../../types/Rune";
import toRuneNameFormat from "../../utils/NameFormatter";

interface PrimaryMasteryTreeProps {
  tree: RuneFamily;
  runes: number[];
  // Ajoutez ici les props nécessaires si besoin
}
// @todo : créer un sous coomposant pour chaque ligne de runes, qui affiche tous les choix possibles et colorie celui qui correspond à la rune choisie
const PrimaryMasteryTree: React.FC<PrimaryMasteryTreeProps> = ({
  tree,
  runes,
}) => {
  const runeList = {
    Domination: [
      ["Electrocute", "Dark Harvest", "Hail Of Blades"],
      ["Cheapshot", "Taste Of Blood", "Sudden Impact"],
      ["Sixth Sense", "Grisly Mementos", "Deep Ward"],
      ["Treasure Hunter", "Relentless Hunter", "Ultimate Hunter"],
    ],
    Sorcery: [
      ["Summon Aery", "Arcane Comet", "Phase Rush"],
      ["Axiom Arcanist", "Manaflow Band", "nimbus cloak"],
      ["Transcendence", "Celerity", "Absolute Focus"],
      ["Scorch", "Waterwalking", "Gathering Storm"],
    ],
    Resolve: [
      ["Grasp Of The Undying", "Aftershock", "Guardian"],
      ["Demolish", "Font of Life", "Shield Bash"],
      ["Conditioning", "Second Wind", "Bone Plating"],
      ["Overgrowth", "Revitalize", "Unflinching"],
    ],
    Inspiration: [
      ["glacial augment", "Unsealed Spellbook", "First Strike"],
      ["Hextech Flashtraption", "Magical Footwear", "Cash Back"],
      ["Triple Tonic", "Time Warp Tonic", "Biscuit Delivery"],
      ["Cosmic Insight", "Approach Velocity", "Jack of All Trades"],
    ],
    Precision: [
      ["Press The Attack", "Lethal Tempo", "Fleet Footwork", "Conqueror"],
      ["Absorb Life", "Triumph", "Presence of Mind"],
      ["Legend: Alacrity", "Legend: Haste", "Legend: Bloodline"],
      ["Coup de Grace", "Cut Down", "Last Stand"],
    ],
  };
  return (
    <Box sx={{ border: "1px solid #ccc", p: 1, maxWidth: "30%" }}>
      <Typography variant="subtitle1" sx={{ mb: 2, color: "#F0E6D2" }}>
        {"Primary"}
      </Typography>
      <Box display={"flex"} flexDirection="row" gap={2}>
        <Box
          component="img"
          src={`https://ddragon.leagueoflegends.com/cdn/img/perk-images/Styles/${tree}/${toRuneNameFormat(
            runeList[tree][0][runes[0]]
          )}/${toRuneNameFormat(runeList[tree][0][runes[0]])}.png`}
          alt="Champion"
          sx={{
            width: "80%", // jouer avec ce param
            height: "auto",
          }}
        />
        <Box
          component="img"
          src={`https://ddragon.leagueoflegends.com/cdn/img/perk-images/Styles/${tree}/${toRuneNameFormat(
            runeList[tree][0][runes[0]]
          )}/${toRuneNameFormat(runeList[tree][0][runes[0]])}.png`}
          alt="Champion"
          sx={{
            width: "80%", // jouer avec ce param
            height: "auto",
          }}
        />
        <Box
          component="img"
          src={`https://ddragon.leagueoflegends.com/cdn/img/perk-images/Styles/${tree}/${toRuneNameFormat(
            runeList[tree][0][runes[0]]
          )}/${toRuneNameFormat(runeList[tree][0][runes[0]])}.png`}
          alt="Champion"
          sx={{
            width: "80%", // jouer avec ce param
            height: "auto",
            filter: "grayscale(1)",
          }}
        />
      </Box>
    </Box>
  );
};

export default PrimaryMasteryTree;
