import React from "react";
import { Box } from "@mui/material";
import { Rune } from "../../types/Rune";

interface RuneRowProps {
  position: number; // which line of the rune family, keystone is 0
  selectedRuneId: number; // which rune to color
  row: Rune[];
  isKeystone: boolean; // Ã  rendre obligatoire 
}
const RuneRow: React.FC<RuneRowProps> = ({ selectedRuneId, row, isKeystone }) => {
  const baseURL = "https://ddragon.leagueoflegends.com/cdn/img/";
  return (
    <Box>
      <Box display={"flex"} flexDirection="row" gap={2} justifyContent={"space-evenly"} mb={"7%"} pt={"5%"}>
      {row.map((rune) => (
        <Box
        component="img"
        src={baseURL+rune.icon}
        alt={rune.name}
        sx={{
          width: (isKeystone)?((row.length==3)?"25%":"20%"):"12%", 
          filter: (selectedRuneId == rune.id)? "none":'grayscale(1)',
          color: "#F0E6D2",
        }}
      />))}
      </Box>
      
    </Box>
  );
};

