import React from "react";
import { Box, Typography } from "@mui/material";
import Runes from "./StatRunes";
import { Rune, RuneFamily } from "../../types/Rune";
import toRuneNameFormat from "../../utils/NameFormatter";
// Ensure TypeScript allows JSON imports by enabling "resolveJsonModule" in tsconfig.json
import runes from "../../utils/runes.json";
interface RuneRowProps {
  tree: number; // 0: Domination, 1: Inspiration, 2: Precision, 3: Resolve, 4: Sorcery
  position: number; // which line of the rune family, keystone is 0
  selectedRune: number; // which rune to color
}
// @todo : récupérer les url depuis le json
// appliquer le filtre gris
const RuneRow: React.FC<RuneRowProps> = ({ tree, selectedRune, position }) => {
  const baseURL = "https://ddragon.leagueoflegends.com/cdn/img/";
  
  console.log(runes[0]["slots"][position]["runes"][0]["name"]);
  return (
    <Box>
      <Box display={"flex"} flexDirection="row" gap={2}>
        <Box
          component="img"
          src={baseURL+runes[tree]["slots"][position]["runes"][0]["icon"]}
          alt={runes[tree]["slots"][position]["runes"][0]["name"]}
          sx={{
            width: "30%", // jouer avec ce param
            height: "auto",
            filter: (selectedRune == 0)? "none":'grayscale(1)',
            color: "#F0E6D2",
          }}
        />
        <Box
          component="img"
          src={baseURL+runes[tree]["slots"][position]["runes"][1]["icon"]}
          alt={runes[tree]["slots"][position]["runes"][1]["name"]}
          sx={{
            width: "30%", // jouer avec ce param
            height: "auto",
            filter: (selectedRune == 1)? "none":'grayscale(1)',
            color: "#F0E6D2",
          }}
        />
        <Box
          component="img"
          src={baseURL+runes[tree]["slots"][position]["runes"][2]["icon"]}
          alt={runes[tree]["slots"][position]["runes"][2]["name"]}
          sx={{
            width: "30%", // jouer avec ce param
            height: "auto",
            filter: (selectedRune == 2)? "none":'grayscale(1)',
            color: "#F0E6D2",
          }}
        />
      </Box>
    </Box>
  );
};

export default RuneRow;
