// turns an element from runes.json to a Rune object
import { Rune } from "../types/Rune";
import { RuneFamily } from "../types/Rune";
import runeList from "../utils/runes.json";

// voir si vraiment nécessaire
// laborieux à implémenter
// remplacerait nameToRuneFamily
function getRuneFamily(key: string): RuneFamily {
  switch (key) {
    case "Press The Attack":
      return RuneFamily.Precision;
    case "":
      return RuneFamily.Domination;
    case "":
      return RuneFamily.Sorcery;
    case "":
      return RuneFamily.Resolve;
    case "":
      return RuneFamily.Inspiration;
    default:
      throw new Error(`Unknown rune family: ${key}`);
  }
}

function nameToRuneFamily(name: string): RuneFamily {
  switch (name) {
    case "Precision":
      return RuneFamily.Precision;
    case "Domination":
      return RuneFamily.Domination;
    case "Sorcery":
      return RuneFamily.Sorcery;
    case "Resolve":
      return RuneFamily.Resolve;
    case "Inspiration":
      return RuneFamily.Inspiration;
    default:
      throw new Error(`Unknown rune family: ${name}`);
  }
}

// turns an element from runes.json to a Rune object
function toRuneFormat(
  id: number,
  icon: string,
  name: string,
  slots: number,
  family: string
): Rune {
  const formattedRune: Rune = {
    id: id,
    name: name,
    icon: icon,
    runeFamily: nameToRuneFamily(family),
    slot: slots,
  };
  return formattedRune;
}

export {toRuneFormat};


function toRunePack(tree: keyof typeof runeList): Rune[][] {
  const treeFromJson = runeList[0] as {
    id: number;
    key: string;
    icon: string;
    name: string;
    slots: { runes: { id: number; key: string; icon: string; name: string; shortDesc: string; longDesc: string; }[]; }[];
  }; // without "as", variable would also have type number, causing error
  let returnValue: Rune[][] = [];
  for (let i = 0; i < treeFromJson.slots.length; i++) {
    let runeRow: Rune[] = [];
    for (let j = 0; j < treeFromJson.slots[i].runes.length; j++) {
      const rune = treeFromJson.slots[i].runes[j];
      const formattedRune = toRuneFormat(
        rune.id,
        rune.icon,
        rune.name,
        i,
        treeFromJson.key
      );
      runeRow.push(formattedRune);
    }
    returnValue.push(runeRow);
  }

  return returnValue;
}

export { toRunePack };
// export default toRuneFormat;
