import React from "react";
import { Box, Typography } from "@mui/material";
import RuneRow from "./RuneRow";
import { toRuneFormat } from "../../utils/RuneFormatter";

interface StatModsProps {
  selectedRuneId: number[];
}

const Runes: React.FC<StatModsProps> = ({ selectedRuneId }) => {
  // THIS IS A TEMPORARY SOLUTION
  // a cleaner but still not perfect solution would be to create a new json file with the stat mods
  const runeList = [
    [
      toRuneFormat(
        5008,
        "StatModsAdaptiveForceIcon.png".toLowerCase(),
        "adap",
        0,
        "StatMods"
      ),
      toRuneFormat(
        5007,
        "StatModsCDRScalingIcon.png".toLowerCase(),
        "CDR",
        0,
        "StatMods"
      ),
      toRuneFormat(
        5005,
        "StatModsAttackSpeedIcon.png".toLowerCase(),
        "AS",
        0,
        "StatMods"
      ),
    ],
    [
      toRuneFormat(
        5008,
        "StatModsAdaptiveForceIcon.png".toLowerCase(),
        "adap",
        0,
        "StatMods"
      ),
      toRuneFormat(
        5007,
        "StatModsCDRScalingIcon.png".toLowerCase(),
        "CDR",
        0,
        "StatMods"
      ),
      toRuneFormat(
        5005,
        "StatModsAttackSpeedIcon.png".toLowerCase(),
        "AS",
        0,
        "StatMods"
      ),
    ],
    [
      toRuneFormat(
        5008,
        "StatModsAdaptiveForceIcon.png".toLowerCase(),
        "adap",
        0,
        "StatMods"
      ),
      toRuneFormat(
        5007,
        "StatModsCDRScalingIcon.png".toLowerCase(),
        "CDR",
        0,
        "StatMods"
      ),
      toRuneFormat(
        5005,
        "StatModsAttackSpeedIcon.png".toLowerCase(),
        "AS",
        0,
        "StatMods"
      ),
    ],
  ];

  return (
    <Box sx={{ border: "1px solid #ccc", p: 1 }}>
      {runeList.map((rune) => (
        <RuneRow
          key={rune[0].id}
          selectedRuneId={selectedRuneId[0]}
          row={rune}
          isKeystone={false}
        />
      ))}
    </Box>
  );
};

export default Runes;

