import React from "react";
import { Box, Typography } from "@mui/material";
import Runes from "./StatRunes";
import { Rune, RuneFamily } from "../../types/Rune";
import toRuneNameFormat from "../../utils/NameFormatter";
// Ensure TypeScript allows JSON imports by enabling "resolveJsonModule" in tsconfig.json
import runes from "../../utils/runes.json";
interface RuneRowProps {
  tree: number;
  runeSet: number[];
  position: number;
}
// @todo : récupérer les url depuis le json
// appliquer le filtre gris
const RuneRow: React.FC<RuneRowProps> = ({ tree, runeSet: runeSet, position }) => {
  const baseURL = "https://ddragon.leagueoflegends.com/cdn/img/";
  
  const runeList = {
    Domination: [
      ["Electrocute", "Dark Harvest", "Hail Of Blades"],
      ["Cheap Shot", "Taste Of Blood", "Sudden Impact"],
      ["Sixth Sense", "Grisly Mementos", "Deep Ward"],
      ["Treasure Hunter", "Relentless Hunter", "Ultimate Hunter"],
    ],
    Sorcery: [
      ["Summon Aery", "Arcane Comet", "Phase Rush"],
      ["Axiom Arcanist", "Manaflow Band", "Nimbus Cloak"],
      ["Transcendence", "Celerity", "Absolute Focus"],
      ["Scorch", "Waterwalking", "Gathering Storm"],
    ],
    Resolve: [
      ["Grasp Of The Undying", "Aftershock", "Guardian"],
      ["Demolish", "Font Of Life", "Shield Bash"],
      ["Conditioning", "Second Wind", "Bone Plating"],
      ["Overgrowth", "Revitalize", "Unflinching"],
    ],
    Inspiration: [
      ["Glacial Augment", "Unsealed Spellbook", "First Strike"],
      ["Hextech Flashtraption", "Magical Footwear", "Cash Back"],
      ["Triple Tonic", "Time Warp Tonic", "Biscuit Delivery"],
      ["Cosmic Insight", "Approach Velocity", "Jack Of All Trades"],
    ],
    Precision: [
      ["Press The Attack", "Lethal Tempo", "Fleet Footwork", "Conqueror"],
      ["Absorb Life", "Triumph", "Presence Of Mind"],
      ["Legend: Alacrity", "Legend: Haste", "Legend: Bloodline"],
      ["Coup De Grace", "Cut Down", "Last Stand"],
    ],
  };
  console.log(runes[0]["slots"][position]["runes"][0]["name"]);
  return (
    <Box>
      <Box display={"flex"} flexDirection="row" gap={2}>
        <Box
          component="img"
          src={baseURL+runes[tree]["slots"][position]["runes"][0]["icon"]}
          //alt={runeList[tree][position][runeSet[0]]}
          sx={{
            width: "30%", // jouer avec ce param
            height: "auto",
            filter: "grayscale(1)",
            color: "#F0E6D2",
          }}
        />
        <Box
          component="img"
          src={baseURL+runes[tree]["slots"][position]["runes"][1]["icon"]}
          //alt={runeList[tree][position][1]}
          sx={{
            width: "30%", // jouer avec ce param
            height: "auto",
            filter: "grayscale(1)",
            color: "#F0E6D2",
          }}
        />
        <Box
          component="img"
          src={baseURL+runes[tree]["slots"][position]["runes"][2]["icon"]}
          //alt={runeList[tree][position][2]}
          sx={{
            width: "30%", // jouer avec ce param
            height: "auto",
            color: "#F0E6D2",
          }}
        />
      </Box>
    </Box>
  );
};

export default RuneRow;
