import React from "react";
import { Box, Typography } from "@mui/material";
import { Spell } from "../../../types/Spell";

interface SpellRowProps {
  spell: Spell;
  championName: string;
  levels: number[];
}

const SpellRow: React.FC<SpellRowProps> = ({ spell, championName, levels }) => {
  const baseURL =
    "https://raw.communitydragon.org/latest/plugins/rcp-be-lol-game-data/global/default/assets/characters/" +
    championName +
    "/hud/icons2d/" +
    championName;
  return (
    <Box sx={{ display: "flex", gap: 1, flexDirection: "row" }}>
      <Box component={"img"} src={baseURL + spell.spellKey + ".png"} />
      {Array.from({ length: 18 }, (_, index) => (
        <Box
          key={index}
          sx={{
            backgroundColor:
              levels.indexOf(index + 1) != -1 ? "#005A82" : "#F0E6D",
          }}
        >
          <Typography
            variant="body1"
            sx={{
              color: "#F0E6D2",
              fontSize: "0.8rem",
              textAlign: "center",
              width: "100%",
            }}
          >
            {index + 1}
          </Typography>
        </Box>
      ))}
    </Box>
  );
};

export default SpellRow;
