import React from "react";
import {
  Box,
  Grid,
  Button,
  Typography,
  Select,
  MenuItem,
} from "@mui/material";
import HomeIcon from "@mui/icons-material/Home";
import { useParams } from "react-router-dom";


interface RouteParams {
  [key: string]: string | undefined;
  championName?: string;
}

const ChampionDetails: React.FC = () => {
  const { championName } = useParams<RouteParams>();
  return (
    <Box sx={{ p: 2 }}>
      {/* Ligne supérieure : Bouton Home à gauche et barre de recherche centrée */}
      <Grid container spacing={2} alignItems="center">
        {/* Bouton Home */}
        <Grid item xs={3}>
          <Button variant="outlined" startIcon={<HomeIcon />}>
            Home
          </Button>
        </Grid>
        {/* Barre de recherche */}
        <Grid item xs={6}>
          <input
            type="text"
            placeholder="Rechercher un champion..."
            style={{
              width: "100%",
              padding: "8px",
              borderRadius: "4px",
              border: "1px solid #ccc",
            }}
          />
        </Grid>
        {/* Optionnel (espace réservé) */}
        <Grid item xs={3}></Grid>
      </Grid>

      {/* Bandeau horizontal avec le titre (nom du champion), statistiques et sélecteur d'elo */}
      <Box
        sx={{
          my: 2,
          p: 1,
          backgroundColor: "#f0f0f0",
          borderRadius: "4px",
        }}
      >
        <Grid container alignItems="center" justifyContent="space-between">
          <Grid item>
            <Typography variant="h5">Nom du Champion</Typography>
          </Grid>
          <Grid item>
            <Typography variant="body1">Winrate: 52%</Typography>
          </Grid>
          <Grid item>
            <Typography variant="body1">Pickrate: 12%</Typography>
          </Grid>
          <Grid item>
            <Typography variant="body1">Banrate: 6%</Typography>
          </Grid>
          <Grid item>
            <Select defaultValue="bronze" size="small">
              <MenuItem value="bronze">Bronze</MenuItem>
              <MenuItem value="silver">Silver</MenuItem>
              <MenuItem value="gold">Gold</MenuItem>
              <MenuItem value="challenger">Challenger</MenuItem>
            </Select>
          </Grid>
        </Grid>
      </Box>

      {/* Bandeau de boutons de navigation */}
      <Grid container spacing={1} sx={{ mb: 2 }}>
        {["Overview", "Items", "Masteries", "Sums", "Spells", "Stats"].map(
          (label) => (
            <Grid item key={label}>
              <Button variant="text">{label}</Button>
            </Grid>
          )
        )}
      </Grid>

      {/* Zone principale : deux grandes colonnes */}
      <Grid container spacing={2}>
        {/* Colonne de gauche : Bouton Home + image du champion (déjà présent en haut, mais ici on le réaffiche dans le layout global) */}
        <Grid item xs={3}>
          <Box sx={{ display: "flex", flexDirection: "column", gap: 2 }}>
            {/* Bouton Home (optionnel si déjà présent en haut) */}
            <Button variant="outlined" startIcon={<HomeIcon />}>
              Home
            </Button>
            {/* Image verticale du champion */}
            <Box
              component="img"
              src="/league-of-legends-icon-default.png"
              alt="Champion"
              sx={{
                width: "100%",
                maxWidth: "200px",
                border: "1px solid #ccc",
                borderRadius: "4px",
              }}
            />
          </Box>
        </Grid>

        {/* Colonne de droite : Détails et contenus divers */}
        <Grid item xs={9}>
          <Grid container spacing={2}>
            {/* Première rangée : Zone des arbres de maîtrises */}
            <Grid item xs={8}>
              {/* Arbre de maîtrises principales */}
              <Box
                sx={{
                  border: "1px solid #ccc",
                  p: 1,
                  minHeight: "300px",
                }}
              >
                <Typography variant="subtitle1">
                  Arbre de maîtrises principales
                </Typography>
                {/* Contenu à implémenter */}
              </Box>
            </Grid>
            <Grid item xs={4}>
              <Box sx={{ display: "flex", flexDirection: "column", gap: 2 }}>
                {/* Arbre de maîtrises secondaires */}
                <Box
                  sx={{
                    border: "1px solid #ccc",
                    p: 1,
                    minHeight: "140px",
                  }}
                >
                  <Typography variant="subtitle1">
                    Arbre de maîtrises secondaires
                  </Typography>
                  {/* Contenu à implémenter */}
                </Box>
                {/* Spell Order */}
                <Box
                  sx={{
                    border: "1px solid #ccc",
                    p: 1,
                    minHeight: "100px",
                  }}
                >
                  <Typography variant="subtitle1">Spell Order</Typography>
                  {/* Contenu à implémenter */}
                </Box>
              </Box>
            </Grid>

            {/* Deuxième rangée : Items, Runes et Invocateurs */}
            <Grid item xs={12}>
              <Grid container spacing={2}>
                {/* Runes */}
                <Grid item xs={3}>
                  <Box sx={{ border: "1px solid #ccc", p: 1 }}>
                    <Typography variant="subtitle1">Runes</Typography>
                    {/* Contenu à implémenter */}
                  </Box>
                </Grid>
                {/* Sorts d'invocateur */}
                <Grid item xs={3}>
                  <Box sx={{ border: "1px solid #ccc", p: 1 }}>
                    <Typography variant="subtitle1">
                      Sorts d'invocateur
                    </Typography>
                    {/* Contenu à implémenter */}
                  </Box>
                </Grid>
                {/* Zone Items : Start Items, Core Build et Late Game Items */}
                <Grid item xs={6}>
                  <Box sx={{ display: "flex", flexDirection: "column", gap: 2 }}>
                    {/* Start Items */}
                    <Box sx={{ border: "1px solid #ccc", p: 1 }}>
                      <Typography variant="subtitle1">
                        Objets de départ
                      </Typography>
                      {/* Contenu à implémenter */}
                    </Box>
                    {/* Core Build */}
                    <Box sx={{ border: "1px solid #ccc", p: 1 }}>
                      <Typography variant="subtitle1">Core Build</Typography>
                      {/* Contenu à implémenter */}
                    </Box>
                    {/* Late Game Items */}
                    <Box sx={{ border: "1px solid #ccc", p: 1 }}>
                      <Typography variant="subtitle1">
                        Late Game Items
                      </Typography>
                      {/* Contenu à implémenter */}
                    </Box>
                  </Box>
                </Grid>
              </Grid>
            </Grid>
          </Grid>
        </Grid>
      </Grid>
    </Box>
  );
};

export default ChampionDetails;
