import React from "react";
import { Box } from "@mui/material";
import { Rune } from "../../types/Rune";

interface RuneRowProps {
  tree: number; // 0: Domination, 1: Inspiration, 2: Precision, 3: Resolve, 4: Sorcery
  position: number; // which line of the rune family, keystone is 0
  selectedRuneId: number; // which rune to color
  row: Rune[];
  isKeystone?: boolean; // Ã  rendre obligatoire 
}
const RuneRow: React.FC<RuneRowProps> = ({ tree, selectedRuneId: selectedRune, position, row, isKeystone }) => {
  const baseURL = "https://ddragon.leagueoflegends.com/cdn/img/";
  return (
    <Box>
      <Box display={"flex"} flexDirection="row" gap={2} justifyContent={"space-evenly"} mb={"5%"}>
        <Box
          component="img"
          src={baseURL+row[0].icon}
          alt={row[0].name}
          sx={{
            width: (isKeystone)?"25%":"15%", 
            height: (isKeystone)?"25%":"15%",
            filter: (selectedRune == 0)? "none":'grayscale(1)',
            color: "#F0E6D2",
          }}
        />
        <Box
          component="img"
          src={baseURL+row[1].icon}
          alt={row[1].name}
          sx={{
            width: (isKeystone)?"25%":"15%", 
            height: (isKeystone)?"25%":"15%",
            filter: (selectedRune == 1)? "none":'grayscale(1)',
            color: "#F0E6D2",
          }}
        />
        <Box
          component="img"
          src={baseURL+row[2].icon}
          alt={row[2].name}
          sx={{
            width: (isKeystone)?"25%":"15%",
            height: "auto",
            filter: (selectedRune == 2)? "none":'grayscale(1)',
            color: "#F0E6D2",
          }}
        />
      </Box>
    </Box>
  );
};

export default RuneRow;
