import { Game } from "../../types/Game";
import { PlayerGameStats } from "../../types/PlayerGameStats";

export function getGamesStats(gameData: Game[], playerId: number, playerPosition: number): PlayerGameStats {
  const gameCount = gameData.length;
  //console.log("gameCount: ", gameCount);
  if (gameCount === 0) {
    return {} as PlayerGameStats; // Return an empty object if no games are found
  } else {
    var gameStats: PlayerGameStats = {
      playerId: playerId, 
      playerName: "",
      gameTotal: gameCount,
      winTotal: 0,
      blueSideTotal: 0,
      blueSideWinTotal: 0,
      kda: [0, 0, 0], // [kills, deaths, assists]
      totalGameDuration: 0, // in seconds
      winRate: 0, // percentage
      blueSidePlayRate: 0, // percentage
      averageGameDuration: 0, // in seconds
      averageKills: 0, // average kills per game
      averageDeaths: 0, // average deaths per game
    };

    /*  gameStats = {
            id: 0,
            date: "",
            competition: "",
            teamBlue: "",
            teamRed: "",
            winner: false,
            patch: "",
            gameDuration: 0,
            champions: [[], []],
            golDiff10: [],
            golDiff15: [],
            golDiff20: [],
            golDiff25: [],
            goldDiff30: [],
            firstBlood: [0, 0],
            firstTo6: [0, 0],
            damageDealt: [[], []],
            kda: [[], []],
        };*/
    for (let i = 0; i < gameCount; i++) {
      const game = gameData[i];
      gameStats.gameTotal += 1;
        gameStats.totalGameDuration += game.gameDuration;
        gameStats.kda[0] += game.kda[0][0]; // kills
        gameStats.kda[1] += game.kda[0][1]; // deaths

    }
    return gameStats;
  }
  return {} as PlayerGameStats; // Return an empty object if no games are found
}
